


-- Script dynamique : génère 5 cessions par user (dates entre 2024-01-01 et 2025-11-10)
-- Vérifie/Crée séquences utiles
CREATE SEQUENCE IF NOT EXISTS cin_seq START 301000000000;
CREATE SEQUENCE IF NOT EXISTS dossier_seq START 1;

DO $$
DECLARE
    u RECORD;
    i INT;
    -- Pools de données malgaches plausibles
    first_names TEXT[] := ARRAY[
        'Jean','Marie','Paul','Lala','Sophie','Hery','Fanja','Zaka','Miora','Tovo',
        'Andry','Niry','Rija','Bako','Mamy','Harizo','Ny Aina','Fetana','Solo','Lova'
    ];
    last_names TEXT[] := ARRAY[
        'Rakoto','Rasoa','Randrianarisoa','Ravalomanana','Andrianarivo','Rasolomampionona',
        'Razafindrakoto','Rasanjy','Ratsimbazafy','Rasoarahona','Rabemananjara','Razafy'
    ];
    company_suffixes TEXT[] := ARRAY[
        'SARL','SA','EURL','Société','Entreprise'
    ];
    company_bases TEXT[] := ARRAY[
        'Banky Fampandrosoana Malagasy', 'Société Générale Madagasikara', 'BFV-SG Antananarivo',
        'Madagascar Leasing', 'Compagnie Malagasy de Crédit', 'Tananarive Finance'
    ];
    addresses TEXT[] := ARRAY[
        'Rue Rainilaiarivony, Antaninarenina',
        'Rue Ravoninahitriniarivo, Ankorondrano',
        'Lot II K 45 Ambohijatovo',
        'Lot III B 23 Ankadifotsy',
        'Lot IV M 76 Ambanidia',
        'Lot V N 12 Ambanitsena',
        'Lot VI P 45 Ivato',
        'Ambodin Isotry, Antananarivo',
        'Analakely, Antananarivo',
        'Isoraka, Antananarivo',
        'Mahamasina, Antananarivo',
        'Ambohipo, Antananarivo'
    ];
    request_subjects TEXT[] := ARRAY[
        'Cession de salaire pour prêt personnel',
        'Cession pour achat de moto',
        'Cession pour remboursement dettes familiales',
        'Cession suite à un crédit bancaire',
        'Cession pour financement scolaire',
        'Cession pour achat d outillage agricole',
        'Cession pour frais médicaux'
    ];
    borrower_remarks TEXT[] := ARRAY[
        'Fonctionnaire au Ministère de la Santé',
        'Employée de la Commune Urbaine',
        'Technicien à la JIRAMA',
        'Enseignant à l’ENI',
        'Comptable à la station-service locale',
        'Agriculteur (riziculture)',
        'Petite commerçante au marché de la ville'
    ];

    date_min DATE := DATE '2024-01-01';
    date_max DATE := DATE '2025-11-10';
    total_days INT := (date_max - date_min)::INT;
    d_contrat DATE;
    d_cession DATE;
    numero TEXT;
    borrower_id INT;
    borrower_addr_id INT;
    lender_person_id INT;
    lender_person_addr_id INT;
    lender_legal_id INT;
    lender_legal_addr_id INT;
    new_cid INT;
    company_idx INT;
    pick INT;
    salary NUMERIC(10,2);
    subj TEXT;
BEGIN
    -- Pour garantir des sociétés par TPI, on créera 3 sociétés par TPI la première fois que ce TPI est rencontré.
    FOR u IN SELECT id, id_tpi FROM users LOOP
        -- create 3 legal persons per TPI if not present
        IF NOT EXISTS (SELECT 1 FROM cession_legal_person WHERE id_tpi = u.id_tpi) THEN
            FOR company_idx IN 1..3 LOOP
                VALUES (
                    (company_bases[1 + ((company_idx + (ascii(u.id::text) % array_length(company_bases,1))) % array_length(company_bases,1))] 
                     || ' ' || company_suffixes[1 + (company_idx % array_length(company_suffixes,1))]),
                    u.id_tpi
                )
                RETURNING id INTO lender_legal_id;

                -- insert a plausible address for the legal person
                VALUES (addresses[1 + ((company_idx + (ascii(u.id::text) % array_length(addresses,1))) % array_length(addresses,1))], lender_legal_id)
                RETURNING id INTO lender_legal_addr_id;
            END LOOP;
        END IF;

        -- Générer 5 cessions pour cet user
        FOR i IN 1..5 LOOP
            -- numéro dossier unique
            numero := 'DOS' || LPAD(nextval('dossier_seq')::text, 6, '0');

            -- choisir date_contrat aléatoire
            d_contrat := date_min + (floor(random() * (total_days + 1)))::int;
            -- date_cession: entre date_contrat et min(date_contrat + 30, date_max)
            d_cession := d_contrat + (floor(random() * (LEAST(30, (date_max - d_contrat)::int) + 1)))::int;

            -- sujet
            subj := request_subjects[1 + floor(random() * array_length(request_subjects,1))::int];

            -- Insert cession (avec reimbursed_amount aléatoire)
                numero_dossier, date_contrat, request_subject, reimbursed_amount, date_cession,
                status_cession, signed, id_tpi, id_user
            ) VALUES (
                numero,
                d_contrat,
                subj,
                (round((1000000 + random()*4000000))::numeric(10,2)),
                d_cession,
                0, -- status_cession demandé = 0
                0, -- signed = 0 par défaut
                u.id_tpi,
                u.id
            )
            RETURNING id INTO new_cid;

            -- === Borrower (toujours personne physique) ===
            VALUES (
                last_names[1 + floor(random() * array_length(last_names,1))::int],
                first_names[1 + floor(random() * array_length(first_names,1))::int],
                nextval('cin_seq'),
                (CASE WHEN random() < 0.5 THEN 'GNR1' ELSE 'GNR2' END)
            ) RETURNING id INTO borrower_id;

            -- address du borrower
            VALUES (
                addresses[1 + floor(random() * array_length(addresses,1))::int],
                borrower_id
            ) RETURNING id INTO borrower_addr_id;

            -- Insert borrower record (liée à la cession)
            salary := (400000 + floor(random() * 1600000))::numeric; -- 400k - 2M
            VALUES (
                salary,
                borrower_remarks[1 + floor(random() * array_length(borrower_remarks,1))::int],
                new_cid,
                borrower_id,
                borrower_addr_id
            );

            -- === Lender : soit legal_person (40%), soit natural_person (60%) ===
            IF random() < 0.40 THEN
                -- choisir une legal_person existante pour ce TPI
                SELECT id INTO lender_legal_id FROM cession_legal_person
                WHERE id_tpi = u.id_tpi
                ORDER BY random()
                LIMIT 1;

                -- s'assurer d'avoir une adresse pour cette legal_person
                SELECT id INTO lender_legal_addr_id FROM cession_legal_person_address
                WHERE id_cession_legal_person = lender_legal_id
                LIMIT 1;

                IF lender_legal_addr_id IS NULL THEN
                    VALUES (
                        addresses[1 + floor(random() * array_length(addresses,1))::int],
                        lender_legal_id
                    ) RETURNING id INTO lender_legal_addr_id;
                END IF;

                -- Insert lender row (legal person only)
                    id_cession, id_cession_legal_person, id_cession_legal_person_address, type
                ) VALUES (
                    new_cid, lender_legal_id, lender_legal_addr_id, 'legal_person'
                );

            ELSE
                -- Lender physical person
                VALUES (
                    last_names[1 + floor(random() * array_length(last_names,1))::int],
                    first_names[1 + floor(random() * array_length(first_names,1))::int],
                    nextval('cin_seq'),
                    (CASE WHEN random() < 0.5 THEN 'GNR1' ELSE 'GNR2' END)
                ) RETURNING id INTO lender_person_id;

                VALUES (
                    addresses[1 + floor(random() * array_length(addresses,1))::int],
                    lender_person_id
                ) RETURNING id INTO lender_person_addr_id;

                    id_cession, id_cession_natural_person, id_cession_natural_person_address, type
                ) VALUES (
                    new_cid, lender_person_id, lender_person_addr_id, 'natural_person'
                );
            END IF;
        END LOOP;
    END LOOP;

    RAISE NOTICE 'Génération terminée : 5 cessions par user insérées.';
END$$;

